import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)

def check(s):
    n = len(s)
    i = 0
    cnta = cntb = cntc = 0
    while i < n and s[i] == 'a':
        cnta += 1
        i += 1
    while i < n and s[i] == 'b':
        cntb += 1
        i += 1
    while i < n and s[i] == 'c':
        cntc += 1
        i += 1
    if i != n:
        return False
    if cnta == 0 or cntb == 0 or cntc == 0:
        return False
    if cntc == cnta or cntc == cntb:
        return True
    return False

out = []
for s in data:
    out.append("YES" if check(s) else "NO")

sys.stdout.write("\n".join(out))

