        j += 1
    cb = j - i
    k = j
    while k < n and s[k] == 'c':
        k += 1
    cc = k - j
    if k != n:
        return "NO"
    if ca < 1 or cb < 1 or cc < 1:
        return "NO"
    if cc == ca or cc == cb:
        return "YES"
    return "NO"

data = sys.stdin.read().strip().split()
