import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)

out_lines = []
for tok in data:
    n = int(tok)
    k = n.bit_length() - 1  # floor(log2(n))
    if n & (n - 1) == 0:
        out_lines.append(str(k))
    else:
        out_lines.append(str(k + 1))

sys.stdout.write("\n".join(out_lines))
