import sys

def max_winner_games(n: int) -> int:
    # winner can play at most floor(log2(n)) + (n is not power of two ? 1 : 0)
    # equivalently: highest k such that 2^k <= n, plus extra if n is not exact power of two
    k = n.bit_length() - 1
    if 1 << k == n:
        return k
    return k + 1

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    out = []
    for tok in data:
        n = int(tok)
        out.append(str(max_winner_games(n)))
    sys.stdout.write("\n".join(out))

if __name__ == "__main__":
    main()
