import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
a = [int(next(it)) for _ in range(n)]

if n == 1:
    print(1)
    sys.exit(0)

L = [1]*n
for i in range(1, n):
    if a[i] > a[i-1]:
        L[i] = L[i-1] + 1

R = [1]*n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        R[i] = R[i+1] + 1

ans = max(L)  # no change
for i in range(n):
    if i == 0:
        ans = max(ans, 1 + (R[1] if n > 1 else 0))
    elif i == n-1:
        ans = max(ans, 1 + (L[n-2] if n > 1 else 0))
    else:
        # join by changing a[i] to some integer between a[i-1] and a[i+1]
        if a[i+1] - a[i-1] > 1:
            ans = max(ans, L[i-1] + 1 + R[i+1])
        # or extend one side by changing a[i]
        ans = max(ans, L[i-1] + 1, R[i+1] + 1)

if ans > n:
    ans = n
print(ans)

