#!/usr/bin/env python3
import sys

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it))
    a = [int(next(it)) for _ in range(n)]
    if n == 1:
        print(1)
        return
    L = [1]*n
    for i in range(1,n):
        if a[i] > a[i-1]:
            L[i] = L[i-1]+1
        else:
            L[i] = 1
    R = [1]*n
    for i in range(n-2, -1, -1):
        if a[i] < a[i+1]:
            R[i] = R[i+1]+1
        else:
            R[i] = 1
    ans = max(L)  # without any change
    # try changing one element at each position
    for i in range(n):
        # change a[i] to fit left
        left = L[i-1] if i-1 >= 0 else 0
        right = R[i+1] if i+1 < n else 0
        # option: extend left by changing a[i] to something > a[i-1]
        ans = max(ans, left + 1)
        # option: extend right by changing a[i] to something < a[i+1]
        ans = max(ans, right + 1)
        # option: connect left and right by changing a[i] to a value between a[i-1] and a[i+1]
        if 0 <= i-1 and i+1 < n and a[i+1] - a[i-1] > 1:
            ans = max(ans, left + 1 + right)
    if ans > n:
        ans = n
    print(ans)

if __name__ == "__main__":
    main()

