import sys, math

def best_k_for_pair(a, b):
    if a == b:
        return 0
    d = abs(a - b)
    best_k = 0
    g0 = math.gcd(a, b)
    best_lcm = a // g0 * b
    i = 1
    while i * i <= d:
        if d % i == 0:
            for g in (i, d // i):
                k = (-a) % g
                A = a + k
                B = b + k
                g_ab = math.gcd(A, B)
                l = A // g_ab * B
                if l < best_lcm or (l == best_lcm and k < best_k):
                    best_lcm = l
                    best_k = k
        i += 1
    return best_k

