import sys
from math import gcd

data = [line.strip() for line in sys.stdin.read().strip().splitlines() if line.strip()!=""]
if not data:
    sys.exit(0)
n = int(data[0])
A = data[1].strip()
B = data[2].strip()
m = len(A)
k = len(B)
def lcm(a,b):
    return a//gcd(a,b)*b
L = lcm(m,k)

def beats(x,y):
    return (x=='R' and y=='S') or (x=='S' and y=='P') or (x=='P' and y=='R')

cycle_nike = 0  # Nikephoros losses
cycle_poly = 0  # Polycarpus losses
for i in range(L):
    a = A[i % m]
    b = B[i % k]
    if a == b:
        continue
    if beats(a,b):
        cycle_poly += 1
    else:
        cycle_nike += 1

full = n // L
rem = n % L

nike = cycle_nike * full
poly = cycle_poly * full

for i in range(rem):
    a = A[i % m]
    b = B[i % k]
    if a == b:
        continue
    if beats(a,b):
        poly += 1
    else:
        nike += 1

print(nike, poly)

