import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)

out_lines = []
if len(data) == 2:
    pairs = [data]
else:
    pairs = []
    for i in range(0, len(data), 2):
        if i+1 < len(data):
            pairs.append([data[i], data[i+1]])

for a, b in pairs:
    total = 0
    stubs = 0
    candles = a
    while candles > 0:
        total += candles
        stubs += candles
        candles = stubs // b
        stubs = stubs % b
    out_lines.append(str(total))

sys.stdout.write("\n".join(out_lines))

