data = sys.stdin.read().strip().splitlines()
if not data:
    sys.exit(0)

def longest_dup_substring_length(s):
    n = len(s)
    for L in range(n, 0, -1):
        seen = set()
        for i in range(n - L + 1):
            sub = s[i:i+L]
            if sub in seen:
                return L
            seen.add(sub)
    return 0

results = []
for line in data:
    line = line.strip()
    if line == '':
        continue
    results.append(str(longest_dup_substring_length(line)))

print("\n".join(results))

