import sys
from collections import deque

data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit(0)
it = iter(data)
n = next(it)
m = next(it)
adj = [[] for _ in range(n+1)]
for _ in range(m):
    a = next(it); b = next(it)
    adj[a].append(b)
    adj[b].append(a)

color = [-1]*(n+1)
components = []
for v in range(1, n+1):
    if color[v] != -1:
        continue
    q = deque()
    q.append(v)
    color[v] = 0
    c0 = 0; c1 = 0
    nodes = 0
    bip = True
    while q:
        u = q.popleft()
        nodes += 1
        if color[u] == 0:
            c0 += 1
        else:
            c1 += 1
        for w in adj[u]:
            if color[w] == -1:
                color[w] = color[u]^1
                q.append(w)
            else:
                if color[w] == color[u]:
                    print("0 1")
                    sys.exit(0)
    components.append((nodes, c0, c1))

sum_pairs = 0
for nodes, c0, c1 in components:
    sum_pairs += c0*(c0-1)//2
    sum_pairs += c1*(c1-1)//2

if sum_pairs > 0:
    print(1, sum_pairs)
    sys.exit(0)

s = 0
k = 0
for nodes, c0, c1 in components:
    if nodes == 1:
        s += 1
    elif nodes == 2:
        k += 1

if k == 0:
    # all isolated vertices
    ways = 0
    if s >= 3:
        ways = s*(s-1)*(s-2)//6
    print(3, ways)
else:
    ways = k*s + 2*k*(k-1)
    print(2, ways)
