import sys

data = sys.stdin.read().split()
if not data:
    sys.exit(0)
n = int(data[0])
l = data[1]
r = data[2]

L = [[] for _ in range(26)]
R = [[] for _ in range(26)]
Lq = []
Rq = []

for i, ch in enumerate(l, start=1):
    if ch == '?':
        Lq.append(i)
    else:
        L[ord(ch) - 97].append(i)

for i, ch in enumerate(r, start=1):
    if ch == '?':
        Rq.append(i)
    else:
        R[ord(ch) - 97].append(i)

pairs = []

for c in range(26):
    while L[c] and R[c]:
        pairs.append((L[c].pop(), R[c].pop()))

for c in range(26):
    while L[c] and Rq:
        pairs.append((L[c].pop(), Rq.pop()))

for c in range(26):
    while R[c] and Lq:
        pairs.append((Lq.pop(), R[c].pop()))

while Lq and Rq:
    pairs.append((Lq.pop(), Rq.pop()))

out = []
out.append(str(len(pairs)))
for a, b in pairs:
    out.append(f"{a} {b}")
sys.stdout.write("\n".join(out))

