import sys

def main():
    data = list(map(int, sys.stdin.read().split()))
    if not data:
        return
    it = iter(data)
    n = next(it)
    x = next(it)
    segs = []
    MAXP = 0
    for _ in range(n):
        l = next(it); r = next(it); c = next(it)
        segs.append((l, r, r - l + 1, c))
        if r > MAXP: MAXP = r
    INF = 10**30
    ans = INF

    # Filter out segments with length > x as they cannot be used
    segs = [s for s in segs if s[2] <= x]

    # First pass: partner that ends before current starts
    segs_by_start = sorted(segs, key=lambda s: s[0])
    segs_by_end = sorted(segs, key=lambda s: s[1])
    best = [INF] * (x + 1)  # best[len] minimal cost among segments that have been added
    j = 0  # pointer for segs_by_end to add those with end < current start
    m_end = len(segs_by_end)
    for l, r, length, cost in segs_by_start:
        while j < m_end and segs_by_end[j][1] < l:
            _, _, ln, cst = segs_by_end[j]
            if ln <= x:
                if cst < best[ln]:
                    best[ln] = cst
            j += 1
        need = x - length
        if 1 <= need <= x and best[need] < INF:
            total = cost + best[need]
            if total < ans:
                ans = total

    # Second pass: partner that starts after current ends
    segs_by_start_desc = sorted(segs, key=lambda s: s[0], reverse=True)
    segs_by_end_desc = sorted(segs, key=lambda s: s[1], reverse=True)
    best = [INF] * (x + 1)
    j = 0
    m_start_desc = len(segs_by_start_desc)
    for l, r, length, cost in segs_by_end_desc:
        while j < m_start_desc and segs_by_start_desc[j][0] > r:
            _, _, ln, cst = segs_by_start_desc[j]
            if ln <= x:
                if cst < best[ln]:
                    best[ln] = cst
            j += 1
        need = x - length
        if 1 <= need <= x and best[need] < INF:
            total = cost + best[need]
            if total < ans:
                ans = total

    if ans >= INF:
        print(-1)
    else:
        print(ans)

if __name__ == "__main__":
    main()

