import sys
import threading

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it))
    x = int(next(it))
    segs = []
    for _ in range(n):
        l = int(next(it)); r = int(next(it)); c = int(next(it))
        segs.append((l, r, c))
    from collections import defaultdict
    groups = defaultdict(list)  # length -> list of (l,r,c)
    for l, r, c in segs:
        d = r - l + 1
        groups[d].append((l, r, c))
    # For each length group, sort by r and build arrays of rs and prefix mins of cost
    grp_info = {}
    for d, lst in groups.items():
        lst.sort(key=lambda x: x[1])  # sort by r
        rs = []
        mins = []
        curmin = 10**30
        for l, r, c in lst:
            rs.append(r)
            if c < curmin:
                curmin = c
            mins.append(curmin)
        grp_info[d] = (rs, mins)
    import bisect
    ans = 10**30
    for l, r, c in segs:
        d2 = r - l + 1
        d1 = x - d2
        if d1 <= 0:
            continue
        if d1 not in grp_info:
            continue
        rs, mins = grp_info[d1]
        # need r' < l
        idx = bisect.bisect_left(rs, l) - 1
        if idx >= 0:
            total = c + mins[idx]
            if total < ans:
                ans = total
    if ans >= 10**30:
        print(-1)
    else:
        print(ans)

if __name__ == "__main__":
    main()

