data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit(0)
it = iter(data)
n = next(it)
x = next(it)
segs = []
for _ in range(n):
    l = next(it); r = next(it); c = next(it)
    length = r - l + 1
    if length < x:
        segs.append((l, r, length, c))
if not segs:
    print(-1)
    sys.exit(0)

INF = 10**30
ans = INF

# First pass: find earlier segment with r < l
segs_by_r = sorted(segs, key=lambda t: t[1])  # by r asc
segs_by_l = sorted(segs, key=lambda t: t[0])  # by l asc
best = [INF] * (x + 1)
j = 0
m = len(segs_by_r)
for l, r, length, cost in segs_by_l:
