            if cj < best[lj]:
                best[lj] = cj
        j += 1
    need = x - length
    if 1 <= need <= x - 1 and best[need] < INF:
        cur = cost + best[need]
        if cur < ans:
            ans = cur

# Second pass: find later segment with l > r
segs_by_l_desc = sorted(segs, key=lambda t: t[0], reverse=True)  # by l desc
segs_by_r_desc = sorted(segs, key=lambda t: t[1], reverse=True)  # by r desc
best = [INF] * (x + 1)
j = 0
m = len(segs_by_l_desc)
for l, r, length, cost in segs_by_r_desc:
    while j < m and segs_by_l_desc[j][0] > r:
        lj, rj, lenj, cj = segs_by_l_desc[j]
        if lenj <= x - 1:
            if cj < best[lenj]:
                best[lenj] = cj
        j += 1
    need = x - length
    if 1 <= need <= x - 1 and best[need] < INF:
        cur = cost + best[need]
        if cur < ans:
            ans = cur

if ans == INF:
    print(-1)
else:
    print(ans)

