    vouchers.append((l, r, d, c))

# group by duration
by_d = [[] for _ in range(MAXT+1)]
for l, r, d, c in vouchers:
    by_d[d].append((l, r, c))

INF = 10**30
ans = INF

# For each possible duration a, pair with b = x - a
for a in range(1, x):
    b = x - a
    if b < 1 or b > MAXT: 
