        # prepare min cost starting at or after each position for duration a
        suf = [INF] * (MAXT + 3)
        for l, r, c in by_d[a]:
            if suf[l] > c:
                suf[l] = c
        for i in range(MAXT, 0, -1):
            if suf[i] > suf[i+1]:
                suf[i] = suf[i+1]
        # for vouchers of duration b, try before
        for l2, r2, c2 in by_d[b]:
            # need a voucher with l > r2 -> suf[r2+1]
            best = suf[r2+1]
            if best < INF:
                total = best + c2
                if total < ans:
                    ans = total
    else:
        # a == b: need two disjoint vouchers both from by_d[a]
        # build pref min cost by right end
        pref = [INF] * (MAXT + 3)
        for l, r, c in by_d[a]:
            if pref[r] > c:
                pref[r] = c
        for i in range(1, MAXT+2):
            if pref[i] > pref[i-1]:
                pref[i] = pref[i-1]
        # for each voucher consider it as second, pair with one ending before its l
        for l, r, c in by_d[a]:
            best = pref[l-1]
            if best < INF:
                total = best + c
                if total < ans:
                    ans = total
        # similarly the other direction handled implicitly but above covers unordered pairs

print(-1 if ans >= INF else ans)

