import sys
import math

def is_snowflake(n):
    if n < 7:  # smallest possible is 1 + 2 + 4 = 7 (k=2, m=2)
        return False
    # maximum m: stop when minimal sum for k=2 exceeds n
    for m in range(2, 61):
        if (1 << (m + 1)) - 1 > n:
            break
        # approximate k as integer m-th root of n
        k_approx = int(n ** (1.0 / m))
        # test a small neighborhood around k_approx
        for k in range(max(2, k_approx - 1), k_approx + 3):
            s = 1
            term = 1
            for _ in range(m):
                term *= k
                s += term
                if s > n:
                    break
            if s == n:
                return True
    return False

