import sys, math

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
t = int(next(it))
out_lines = []
for _ in range(t):
    n = int(next(it))
    found = False
    if n >= 7:
        max_m = int(math.log2(n))  # m such that k=2 gives sum ~2^{m+1}-1 <= n => m <= log2(n)
        for m in range(2, max_m + 1):
            lo = 2
            hi = int(round(n ** (1.0 / m))) + 1
            if hi < 2:
                continue
            while lo <= hi:
                mid = (lo + hi) // 2
                s = 1
                cur = 1
                for _ in range(m):
                    cur *= mid
                    s += cur
                    if s > n:
                        break
                if s == n:
                    found = True
                    break
                if s < n:
                    lo = mid + 1
                else:
                    hi = mid - 1
            if found:
                break
    out_lines.append("YES" if found else "NO")
sys.stdout.write("\n".join(out_lines))

