import sys
from collections import deque

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it)); m = int(next(it))
    a = [0]*m
    b = [0]*m
    c = [0]*m
    adj = [[] for _ in range(n+1)]
    degsum = [0]*(n+1)
    for i in range(m):
        ai = int(next(it)); bi = int(next(it)); ci = int(next(it))
        a[i]=ai; b[i]=bi; c[i]=ci
        adj[ai].append((bi,i))
        adj[bi].append((ai,i))
        degsum[ai]+=ci
        degsum[bi]+=ci

    rem = [0]*(n+1)
    for v in range(1,n+1):
        if v==1:
            rem[v]=degsum[v]
        elif v==n:
            rem[v]=0
        else:
            rem[v]=degsum[v]//2

    used = [False]*m
    ans = [0]*m
    q = deque()
    for v in range(1,n+1):
        if rem[v]==0:
            q.append(v)

    while q:
        v = q.popleft()
        for u, eid in adj[v]:
            if used[eid]:
                continue
            used[eid]=True
            # orient edge from u -> v (since v has no remaining outgoing requirement)
            if a[eid]==u and b[eid]==v:
                ans[eid]=0
            else:
                ans[eid]=1
            rem[u]-=c[eid]
            if rem[u]==0:
                q.append(u)

    out = '\n'.join(str(x) for x in ans)
    sys.stdout.write(out)

if __name__ == "__main__":
    main()
