import sys
from itertools import product

def run_befunge(program, input_digits):
    stack = []
    ip = 0  # Instruction pointer
    input_ptr = 0  # Pointer for input digits
    width = len(program)

    while ip < width:
        c = program[ip]

        if c == '?':
            if input_ptr >= len(input_digits):
                return False  # Not enough input
            stack.append(ord(input_digits[input_ptr]))
            input_ptr += 1
        elif c == '+':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a + b)
        elif c == '-':
            if len(stack) < 2:
