MOD = 998244353

def main():
    import sys
    input = sys.stdin.read
    n = int(input())

    if n == 0:
        print(1)
        return

    even_sum = 1  # dp[0] is 1, which is even index (0)
    odd_sum = 0
    dp_n = 0

    for i in range(1, n + 1):
        if i % 2 == 0:
            current_dp = odd_sum
        else:
            current_dp = even_sum

        if i % 2 == 0:
            even_sum += current_dp
        else:
            odd_sum += current_dp

        if i == n:
            dp_n = current_dp

    # Compute 2^n mod MOD
    pow_2n = pow(2, n, MOD)
    # Compute inverse of pow_2n
    inv_pow_2n = pow(pow_2n, MOD - 2, MOD)

    result = (dp_n * inv_pow_2n) % MOD
    print(result)

if __name__ == "__main__":
    main()
