        return -1

    board = [[0] * N for _ in range(N)]
    num = 1
    row, col = 0, 0
    board[row][col] = num
    num += 1

    # Fill the board in a snake-like pattern to ensure minimal jumps
    for i in range(N):
        if i % 2 == 0:
            for j in range(N):
                if i == 0 and j == 0:
