from collections import defaultdict

def longest_substring_twice(s):
    n = len(s)
    seen = defaultdict(set)

    # Try all possible substring lengths from n-1 down to 1
    for length in range(n - 1, 0, -1):
        for i in range(n - length + 1):
            substr = s[i:i+length]
            seen[substr].add(i)
            if len(seen[substr]) >= 2:
                return length
    return 0

if __name__ == "__main__":
    import sys
    input_str = sys.stdin.read().strip()
    print(longest_substring_twice(input_str))
