from collections import defaultdict

def longest_repeated_substring(s):
    n = len(s)
    # Store all substrings and their starting indices
    substring_indices = defaultdict(list)

    # Generate all substrings
    for i in range(n):
        for j in range(i + 1, n + 1):
            substr = s[i:j]
            substring_indices[substr].append(i)

    # Find the longest substring that appears at least twice
    max_length = 0
    for substr, indices in substring_indices.items():
        if len(indices) >= 2:
            max_length = max(max_length, len(substr))
    return max_length

if __name__ == "__main__":
    import sys
    input_str = sys.stdin.read().strip()
    print(longest_repeated_substring(input_str))
