def main():
    import sys
    data = sys.stdin.read().splitlines()
    n = int(data[0].strip())
    s_str = data[1].strip()

    total_teams = 1 << n

    def is_valid(x):
        low = 1
        high = total_teams

        left = 0
        right = total_teams - 1

        def dfs(phase, l, r):
            if l == r:
                return True

            mid = (l + r) // 2
            next_phase = phase + 1
            if next_phase >= n:
                return True

            outcome_char = s_str[n - 1 - next_phase]

            if outcome_char == '1':
                if x < mid:
                    return False
                else:
                    return dfs(next_phase, 2*mid, 2*mid + (r - l))
            else:
                if x > mid:
                    return False
                else:
                    return dfs(next_phase, 2*mid, 2*mid + (r - l))

        return dfs(0, 0, total_teams-1)

    results = []
    for x in range(1, total_teams + 1):
        if is_valid(x):
            results.append(str(x))

    print(" ".join(results))

if __name__ == '__main__':
    main()
```

