def main():
    w, h, k = map(int, input().split())
    total_cells = 0
    for i in range(1, k + 1):
        a = w - 4 * (i - 1)
        b = h - 4 * (i - 1)
        if a < 2 or b < 2:
            break
        total_cells += 2 * (a + b) - 4
    print(total_cells)

if __name__ == '__main__':
    main()
