import sys

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    a = list(map(int, data[1:]))

    max_len = 1  # At least one element

    for i in range(n):
        for j in range(i, n):
            # Reverse subarray from i to j
            new_a = a[:i] + a[i:j+1][::-1] + a[j+1:]
            # Compute length of longest non-decreasing subsequence
            dp = [1] * n
            for k in range(n):
                for l in range(k):
                    if new_a[l] <= new_a[k]:
                        dp[k] = max(dp[k], dp[l] + 1)
            max_len = max(max_len, max(dp))

    print(max_len)

if __name__ == "__main__":
    main()
