def main():
    s = input().strip()
    n = len(s)
    ans = 0
    for L in range(n-1, 0, -1):
        seen = {}
        for i in range(0, n - L + 1):
            substr = s[i:i+L]
            seen[substr] = seen.get(substr, 0) + 1
            if seen[substr] == 2:
                ans = L
                break
        else:
            continue
        break
    print(ans)

if __name__ == '__main__':
    main()
