import sys

def longest_repeated_substring(s):
    n = len(s)
    max_len = 0

    for i in range(n):
        seen = set()
        current = ""
        for j in range(i, n):
            current += s[j]
            if current in seen:
                max_len = max(max_len, len(current) - 1)
                break
            seen.add(current)

    return max_len

def main():
    s = sys.stdin.readline().strip()
    print(longest_repeated_substring(s))

if __name__ == "__main__":
    main()
