import math

def main():
    import sys
    data = sys.stdin.readline().split()
    n = int(data[0])
    a = float(data[1])

    if n == 3:
        print("1 2 3")
        return

    total_angle = 360.0
    edge_angle = total_angle / n

    best_diff = float('inf')
    best_vertices = None

    for center in range(n):
        for d1 in range(1, n//2 + 1):
            v1 = (center - d1) % n
            v2 = center
            for d2 in range(1, n//2 + 1):
                v3 = (center + d2) % n
                if v1 == v2 or v2 == v3 or v1 == v3:
                    continue
                arc1 = d1 * edge_angle
                arc2 = d2 * edge_angle
                intercepted_arc = total_angle - (arc1 + arc2)
                angle_val = intercepted_arc / 2.0

                diff = abs(angle_val - a)
                if diff < best_diff:
                    best_diff = diff
                    best_vertices = (v1+1, v2+1, v3+1)

                if best_diff == 0:
                    break
            if best_diff == 0:
                break

    if best_vertices is None:
        for center in range(n):
            for d1 in range(1, n//2 + 1):
                v1 = (center + d1) % n
                v2 = center
                for d2 in range(1, n//2 + 1):
                    v3 = (center - d2) % n
                    if v1 == v2 or v2 == v3 or v1 == v3:
                        continue
                    arc1 = d1 * edge_angle
                    arc2 = d2 * edge_angle
                    intercepted_arc = total_angle - (arc1 + arc2)
                    angle_val = intercepted_arc / 2.0

                    diff = abs(angle_val - a)
                    if diff < best_diff:
                        best_diff = diff
                        best_vertices = (v1+1, v2+1, v3+1)

    print(f"{best_vertices[0]} {best_vertices[1]} {best_vertices[2]}")

if __name__ == '__main__':
    main()
