def main():
    s = input().strip()

    zeros = []
    ones = []
    twos = []

    for char in s:
        if char == '0':
            zeros.append(char)
        elif char == '1':
            ones.append(char)
        elif char == '2':
            twos.append(char)

    combined = []
    i0 = 0
    i2 = 0
    n0 = len(zeros)
    n2 = len(twos)

    while i0 < n0 and i2 < n2:
        if zeros[i0] < twos[i2]:
            combined.append(zeros[i0])
            i0 += 1
        else:
            combined.append(twos[i2])
            i2 += 1

    while i0 < n0:
        combined.append(zeros[i0])
        i0 += 1

    while i2 < n2:
        combined.append(twos[i2])
        i2 += 1

    result = ''.join(combined + [''.join(ones)])
    print(result)

if __name__ == '__main__':
    main()
