import sys
import math

def is_snowflake(n):
    if n < 2:
        return False
    # Try all possible k from 2 to sqrt(n)
    max_k = int(math.isqrt(n)) + 1
    for k in range(2, max_k + 1):
        total = 1  # initial node
        current = 1
        while True:
            next_nodes = current * k
            if total + next_nodes > n:
                break
            total += next_nodes
            current = next_nodes
        if total == n:
            return True
    return False

