import sys

def solve():
    n, m = map(int, input().split())
    tasks = list(map(int, input().split()))

    max_task = max(tasks)
    min_time = 0
    current_pos = 1

    for task in tasks:
        if task > current_pos:
            min_time += task - current_pos - 1
            min_time += task - 1
            current_pos = task
        else:
            min_time += 1

    print(min_time)

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        solve()
