import sys
from collections import defaultdict

def read_input():
    x1, y1 = map(int, input().split())
    x2, y2 = map(int, input().split())
    n = int(input())

    lines = []
    for _ in range(n):
        a, b, c = map(int, input().split())
        lines.append((a, b, c))

    return x1, y1, x2, y2, n, lines

def get_block(x, y, lines):
    block = 0
    for a, b, c in lines:
        if a * x + b * y + c == 0:
            block += 1
    return block

def solve(x1, y1, x2, y2, n, lines):
    blocks = set()
    for a, b, c in lines:
        blocks.add((a, b))
    blocks = list(blocks)

    queue = [(get_block(x1, y1, lines), (x1, y1))]
    visited = {(get_block(x1, y1, lines), (x1, y1))}
    steps = 0

    while queue:
        new_queue = []
        for block, (x, y) in queue:
            if (block, (x, y)) == (get_block(x2, y2, lines), (x2, y2)):
                return steps
            for a, b in blocks:
                new_block = get_block(x, y, lines) ^ get_block(x, y + b / a, lines)
                new_x = x
                new_y = y + b / a
                if (new_block, (new_x, new_y)) not in visited:
                    visited.add((new_block, (new_x, new_y)))
                    new_queue.append((new_block, (new_x, new_y)))
        queue = new_queue
        steps += 1

    return -1

def main():
    x1, y1, x2, y2, n, lines = read_input()
    result = solve(x1, y1, x2, y2, n, lines)
    print(result)

if __name__ == "__main__":
    main()
