import sys

def solve():
    m, a, b = map(int, input().split())
    ans = 0
    for i in range(m + 1):
        j = min(i + a, m)
        k = max(0, i - b)
        if j >= k:
            ans += (j - k + 1)
    print(ans)

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        solve()
