import sys
from collections import defaultdict

def dfs(node, parent, tree):
    for child in tree[node]:
        if child != parent:
            dfs(child, node, tree)

def find_max_subtree(root, tree):
    max_subtree = 0
    max_node = root

    stack = [(root, -1)]
    visited = set()

    while stack:
        node, parent = stack.pop()
        visited.add(node)

        if node == root and len(visited) > max_subtree:
            max_subtree = len(visited)
            max_node = root

        for child in tree[node]:
            if child != parent and child not in visited:
                stack.append((child, node))

    return max_node

def solve(n, k, edges):
    tree = defaultdict(list)
    for u, v in edges:
        tree[u].append(v)
        tree[v].append(u)

    max_nodes = [None] * n
    for i in range(n):
        max_nodes[i] = find_max_subtree(i, tree)

    removed = []
    for i in range(k):
        max_node = max_nodes[n - i - 1]
        removed.append(max_node + 1)
        max_nodes[max_node] = None

    return ' '.join(map(str, removed))

lines = sys.stdin.read().splitlines()
n_k = lines[0].split()
n, k = int(n_k[0]), int(n_k[1])
edges = [line.split() for line in lines[1:]]

for i in range(len(edges)):
    edges[i] = [int(x) - 1 for x in edges[i]]

result = [solve(n, k, edges) for _ in range(len(edges))]
print('\n'.join(result))
