import sys

def golden_plate():
    w, h, k = map(int, input().split())

    total_cells = 0
    for i in range(1, k + 1):
        inner_w = w - 4 * (i - 1)
        inner_h = h - 4 * (i - 1)
        if inner_w > 0 and inner_h > 0:
            total_cells += (inner_w + inner_h) * 2 - 4 * i + 1

    print(total_cells)

if __name__ == "__main__":
    for _ in range(int(input())):
        golden_plate()
