from collections import defaultdict

def has_koutsu(hand):
    """Check if the hand contains a koutsu."""
    counts = defaultdict(int)
    for tile in hand:
        counts[tile[0]] += 1
    return any(count == 3 for count in counts.values())

def has_shuntsu(hand):
    """Check if the hand contains a shuntsu."""
    suits = defaultdict(list)
    for tile in hand:
        suits[tile[1]].append(int(tile[0]))
    for suit in suits.values():
        suit.sort()
        if len(suit) >= 3 and all(suit[i] + 1 == suit[i+1] for i in range(2)):
            return True
    return False

def solve(hand):
    """Solve the problem for a given hand."""
    if has_koutsu(hand) or has_shuntsu(hand):
        return 0
    else:
        return 1

