def find_min_tiles(hand):
    # Sort the hand to easily find sequences
    hand.sort()

    # Check for triplets
    for i in range(len(hand) - 2):
        if hand[i][0] == hand[i+1][0] == hand[i+2][0]:
            return 0

    # Check for sequences
    for i in range(len(hand) - 2):
        if hand[i][0] + 1 == hand[i+1][0] and hand[i+1][0] + 1 == hand[i+2][0]:
            return 0

    # If no triplets or sequences are found, at least one more tile is needed
    return 1

# Read input from standard input
hand = []
for _ in range(3):
    hand.append(input().split())

# Print the minimum number of extra tiles needed for each hand
for h in hand:
    print(find_min_tiles(h))
