import sys

def array_stabilization(n, a):
    a.sort()
    min_diff = a[1] - a[0]
    max_diff = a[n-1] - a[n-2]

    for i in range(1, n-1):
        diff = a[i+1] - a[i-1]
        if diff < min_diff:
            min_diff = diff

    return max(min_diff, max_diff)

def main():
    input_lines = sys.stdin.read().split('\n')
    n = int(input_lines[0])
    a = list(map(int, input_lines[1].split()))

    print(array_stabilization(n, a))

if __name__ == "__main__":
    main()
