import sys

def solve():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))

    prefix_sum = [0] * (n + 1)
    for i in range(n):
        prefix_sum[i + 1] = prefix_sum[i] + a[i]

    total_sum = 0
    for i in range(n - k + 1):
        total_sum += prefix_sum[i + k] - prefix_sum[i]

    print(total_sum / (n - k + 1))

if __name__ == "__main__":
    t = 1  # int(input())
    for _ in range(t):
        solve()
