import sys

def read_input():
    n = int(input())
    a = list(map(int, input().split()))
    m_str = input()
    return n, a, m_str

def calculate_max_value(n, a, m_str):
    max_val = 0
    m = 0
    for i in range(n):
        if m_str[i] == '1':
            max_val += a[n - 1 - i]
            m += 2 ** i
    total_sum = sum(a)
    if total_sum > m:
        max_val = total_sum
    return max_val

def main():
    n, a, m_str = read_input()
    max_val = calculate_max_value(n, a, m_str)
    print(max_val)

if __name__ == "__main__":
    main()
