import sys

def read_input():
    n = int(input())
    a = list(map(int, input().split()))
    return n, a

def solve(n, a):
    # Sort the array in descending order
    a.sort(reverse=True)

    # Initialize the result array with the first element of a
    b = [a[0]]

    # Initialize the current sum as the first element of a
    curr_sum = a[0]

    # Iterate over the rest of the elements in a
    for i in range(1, n):
        # Find the next element in a that has a different sum
        for j in range(i, n):
            if (curr_sum + a[j]) % 2 != 0:
                b.append(a[j])
                curr_sum += a[j]
                break

    # If we couldn't find a valid permutation, return -1
    if len(b) != n:
        return -1

    return ' '.join(map(str, b))

def main():
    n, a = read_input()
    result = solve(n, a)
    print(result)

if __name__ == "__main__":
    main()
