import sys

def read_input():
    n, l = map(int, input().split())
    a = list(map(int, input().split()))
    return n, l, a

def solve(n, l, a):
    a.sort()

    max_left = 0
    for i in range(1, n + 1):
        if i < n:
            max_left = max(max_left, a[i] - a[i - 1])
        else:
            max_left = max(max_left, l - a[i - 1])

    max_right = 0
    for i in range(n - 1, -1, -1):
        if i > 0:
            max_right = max(max_right, a[i + 1] - a[i])
        else:
            max_right = max(max_right, a[i])

    return (max(max_left, max_right) + 1) / 2

def main():
    t = 1
    for _ in range(t):
        n, l, a = read_input()
        result = solve(n, l, a)
        print(f"{result:.10f}")

if __name__ == "__main__":
    main()
```

