import sys

def solve():
    s = input()
    n = len(s)
    a = s.count('a')
    b = s.count('b')

    if a == 0 or b == 0:
        print("NO")
        return

    for i in range(n):
        if s[i] == 'c':
            break

    c = n - i
    if c > max(a, b):
        print("NO")
        return

    p = [a, b, c]
    p.sort()

    if p[0] == p[1] and p[1] == p[2]:
        print("YES")
        return
    elif p[0] == p[1] and s[:i].count('a') == p[0] and s[i:].count('b') == p[1]:
        print("YES")
        return
    elif p[1] == p[2] and s[:i].count('b') == p[1] and s[i:].count('a') == p[2]:
        print("YES")
        return
    else:
        print("NO")

if __name__ == "__main__":
    t = 1
    while t > 0:
        solve()
        t -= 1
