import sys

def read_input():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))
    return n, k, a

def solve(n, k, a):
    dp = [[0] * (n + 1) for _ in range(k + 1)]
    max_val = [0] * (k + 1)
    for i in range(1, k + 1):
        max_val[i] = float('-inf')
    for i in range(1, n + 1):
        for j in range(1, min(i + 1, k + 1)):
            max_val[j] = max(max_val[j], a[i - 1])
            dp[j][i] = max(dp[j][i - 1], dp[j - 1][i - 1] + max_val[j])
    t = [0] * k
    i, j = k, n
    while i > 0 and j > 0:
        if dp[i][j] == dp[i][j - 1]:
            t[i - 1] += 1
            j -= 1
        else:
            i -= 1
            j -= 1
            t[i] += 1
    return dp[k][n], t

def print_output(n, k, a, result):
    total_profit, t = result
    print(total_profit)
    print(' '.join(map(str, t)))

if __name__ == "__main__":
    n, k, a = read_input()
    result = solve(n, k, a)
    print_output(n, k, a, result)
