import sys

def read_input():
    n = int(input())
    a = list(map(int, input().split()))
    return n, a

def solve(n, a):
    max_length = 1
    current_length = 1

    for i in range(1, n):
        if a[i] > a[i - 1]:
            current_length += 1
            max_length = max(max_length, current_length)
        else:
            current_length = 1

    current_length = 1

    for i in range(n - 2, -1, -1):
        if a[i] < a[i + 1]:
            current_length += 1
            max_length = max(max_length, current_length)
        else:
            current_length = 1

    for i in range(1, n - 1):
        if a[i] <= a[i - 1] or a[i] >= a[i + 1]:
            max_length = max(max_length, n - 1)
        elif a[i - 1] < a[i + 1]:
            max_length = max(max_length, n - 1)

    return max_length

def main():
    n, a = read_input()
    print(solve(n, a))

if __name__ == "__main__":
    main()
