import sys

def solve():
    n = int(input())
    a = list(map(int, input().split()))

    max_len = 1
    prev_diff = float('inf')
    last_change = -1

    for i in range(1, n):
        if a[i] <= a[i-1]:
            if last_change == i - 1:
                max_len = max(max_len, i)
            elif prev_diff == 1 or last_change == -1:
                max_len = max(max_len, i + 1)
            else:
                max_len = max(max_len, i)
            last_change = i
        else:
            max_len = max(max_len, i - last_change)
            prev_diff = a[i] - a[i-1]

    return max_len

print(solve())
